        </div>
      </div>
    </motion.div>
  );
};

// Main Content Creator Component
const ContentCreator = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [visualStyles, setVisualStyles] = useState([]);
  const [hooks, setHooks] = useState([]);
  const [scripts, setScripts] = useState([]);
  const [selectedStyle, setSelectedStyle] = useState(null);
  const [selectedHook, setSelectedHook] = useState(null);
  const [selectedScripts, setSelectedScripts] = useState([undefined, undefined, undefined, undefined, undefined]);
  const [currentScriptIndex, setCurrentScriptIndex] = useState(0);
  const [lastUpdated] = useState(new Date().toLocaleString('de-DE'));
  const [lightboxImage, setLightboxImage] = useState(null);

  const steps = [
    { id: 1, label: "Visual Style", sublabel: "Choose your style" },
    { id: 2, label: "Hook", sublabel: "Select your hook" },
    { id: 3, label: "Scripts", sublabel: "Pick 5 scripts" },
    { id: 4, label: "Summary", sublabel: "Review & copy" }
  ];

  useEffect(() => {
    // Load data from local JSON file
    setVisualStyles(contentData.visualStyles);
    setHooks(contentData.hooks);
    setScripts(contentData.scripts);
  }, []);

  const handleScriptSelect = (script, index, remove = false) => {
    if (remove) {
      const newScripts = [...selectedScripts];
      newScripts[index] = undefined;
      setSelectedScripts(newScripts);
      setCurrentScriptIndex(index);
      return;
    }

    const newScripts = [...selectedScripts];
    newScripts[index] = script;
    setSelectedScripts(newScripts);

    // Move to next empty slot
    const nextEmpty = newScripts.findIndex((s, i) => i > index && s === undefined);
    if (nextEmpty !== -1) {
      setCurrentScriptIndex(nextEmpty);
    } else {
      // Find first empty slot
      const firstEmpty = newScripts.findIndex(s => s === undefined);
      if (firstEmpty !== -1) {
        setCurrentScriptIndex(firstEmpty);
      }
    }
  };

  const canProceed = () => {
    switch (currentStep) {
      case 0: return selectedStyle !== null;
      case 1: return selectedHook !== null;
      case 2: return selectedScripts.filter(s => s !== undefined).length === 5;
      default: return true;
    }
  };

  const handleNext = () => {
    if (canProceed() && currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handleBack = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleBackToEdit = () => {
    setCurrentStep(0);
  };

  return (
    <div className="min-h-screen" data-testid="content-creator">
      <Toaster position="top-right" />

      {/* Global Lightbox */}
      {lightboxImage && (
        <ImageLightbox
          images={lightboxImage}
          onClose={() => setLightboxImage(null)}
        />
      )}

      {/* Header */}
      <header className="bg-white/80 backdrop-blur-md border-b border-slate-100 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-8 h-8 bg-[#FF7870] rounded-lg flex items-center justify-center">
              <span className="text-white font-bold text-lg">V</span>
            </div>
            <span className="font-semibold text-[#0F172A] text-lg">She's Viral</span>
          </div>
          <div className="flex items-center gap-4">
            <span className="text-sm text-slate-500">Welcome, <span className="font-medium text-[#0F172A]">johansimon</span></span>
            <button className="flex items-center gap-2 text-[#FF7870] hover:text-[#E66A63] text-sm font-medium" data-testid="sign-out-btn">
              <LogOut className="w-4 h-4" />
              Sign Out
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-6 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-[#0F172A]">Content Dashboard</h1>
          <p className="text-slate-500 text-sm">Last updated: {lastUpdated}</p>
        </div>

        {/* Content Creator Card */}
        <div className="bg-white rounded-3xl border border-slate-100 shadow-2xl shadow-slate-200/50 p-8 md:p-12">
          <h2 className="text-2xl font-bold text-center mb-8 text-[#0F172A]">Content Creator</h2>

          <Stepper currentStep={currentStep} steps={steps} />

          <AnimatePresence mode="wait">
            {currentStep === 0 && (
              <VisualStyleStep
                key="visual"
                visualStyles={visualStyles}
                selectedStyle={selectedStyle}
                onSelect={setSelectedStyle}
                onImageClick={setLightboxImage}
              />
            )}
            {currentStep === 1 && (
              <HookStep
                key="hook"
                hooks={hooks}
                selectedHook={selectedHook}
                onSelect={setSelectedHook}
                selectedStyle={selectedStyle}
              />
            )}
            {currentStep === 2 && (
              <ScriptStep
                key="scripts"
                scripts={scripts}
                selectedScripts={selectedScripts}
                onSelect={handleScriptSelect}
                currentScriptIndex={currentScriptIndex}
                setCurrentScriptIndex={setCurrentScriptIndex}
              />
            )}
            {currentStep === 3 && (
              <SummaryStep
                key="summary"
                selectedStyle={selectedStyle}
                selectedHook={selectedHook}
                selectedScripts={selectedScripts}
                onBackToEdit={handleBackToEdit}
              />
            )}
          </AnimatePresence>
        </div>

        {/* Navigation Buttons - Fixed at bottom */}
        {currentStep < 3 && (
          <div className="fixed bottom-0 left-0 right-0 bg-white/90 backdrop-blur-md border-t border-slate-100 py-4 px-6 z-40">
            <div className="max-w-6xl mx-auto flex justify-between">
              <Button
                variant="outline"
                onClick={handleBack}
                disabled={currentStep === 0}
                className="rounded-full px-8 py-6 font-medium border-slate-200 hover:bg-slate-50"
                data-testid="back-btn"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>

              <Button
                onClick={handleNext}
                disabled={!canProceed()}
                className={`rounded-full px-8 py-6 font-medium ${
                  canProceed() 
                    ? 'bg-[#FF7870] hover:bg-[#E66A63] text-white shadow-lg shadow-pink-200' 
                    : 'bg-slate-100 text-slate-400'
                }`}
                data-testid="next-btn"
              >
                Next
                <ArrowRight className="w-4 h-4 ml-2" />
              </Button>
            </div>
          </div>
        )}

        {/* Spacer for fixed navigation */}
        {currentStep < 3 && <div className="h-24"></div>}
      </main>
    </div>
  );
};

function App() {
  return <ContentCreator />;
}

export default App;
